    @Test
    public void followInstructions_11_expect_button_1985() throws Exception {
        List<Integer> expectedButtons = Arrays.asList(1, 9, 8, 5);
        String instructions = "ULL\n" //
                + "RRDDD\n" //
                + "LURDL\n" //
                + "UUUUD";

        BathroomSecurity bathroomSecurity = new BathroomSecurity();
        List<Integer> buttonToPress = bathroomSecurity.followInstructions(instructions);

        String message = String.format(
                "Expect buttonToPress '%s', but was buttonToPress '%s' after following instructions:\n%s", //
                expectedButtons, buttonToPress, instructions);
        Assert.assertEquals(message, expectedButtons, buttonToPress);
    }


